filters.template:
function (doc, req) {
    if (doc._deleted) {
        return true;
    }
    if (doc.type != 'app') {
        return false;
    }
    if (doc.template != req.query.template) {
        return false;
    }
    return true;
}

apps.map:
function (doc) {
    if (doc.type && doc.type === 'app') {
        emit(doc.organization, {
            name: doc.name,
            template: doc.template
        });
    }
}

organization.map:
function (doc) {
    if (doc.type && doc.type === 'template' && doc.organizations) {
        for (var i = 0; i < doc.organizations.length; i++) {
            emit(doc.organizations[i], doc.name);
        }
    }
}
